mapper.insert:
insert into indicator_info (id, indicator_name, indicator_desc, 
  indicator_return_type, indicator_type, status, 
  creator_id, creator_name, gmt_create, gmt_modify
  )
values (#{id,jdbcType=BIGINT}, #{indicatorName,jdbcType=VARCHAR}, #{indicatorDesc,jdbcType=VARCHAR}, 
  #{indicatorReturnType,jdbcType=SMALLINT}, #{indicatorType,jdbcType=SMALLINT}, #{status,jdbcType=SMALLINT}, 
  #{creatorId,jdbcType=BIGINT}, #{creatorName,jdbcType=VARCHAR}, #{gmtCreate,jdbcType=TIMESTAMP}, #{gmtModify,jdbcType=TIMESTAMP}
  )

mapper.select:
select

from indicator_info

  id = #{indicatorId}

    and status = #{status}


limit 1

select

from indicator_info

  id in

    #{indicatorId}


    and status = #{status}
